const PHONE_LOADED = 'PHONE_LOADED'
const ADD_PRICE = 'ADD_PRICE'
const ADD_PRODUCT = 'ADD_PRODUCT'
const LOADING = 'LOADING'
const ERROR = 'ERROR'
const MINUS_CART = 'MINUS_CART'
const PLUS_CART = 'PLUS_CART'
const DELETE_CART = 'DELETE_CART'

const phoneLoaded = (newBooks) => {
  return {
    type: PHONE_LOADED,
    payload: newBooks,
  }
}

const addPrice = (price) => {
  return {
    type: ADD_PRICE,
    price: price,
  }
}

const addProduct = (product) => {
  return {
    type: ADD_PRODUCT,
    product: product,
  }
}
const addLoading = (loading) => {
  return {
    type: LOADING,
    loading,
  }
}
const addError = (error) => {
  return {
    type: ERROR,
    error,
  }
}
const minusCart = (id) => {
  return {
    type: MINUS_CART,
    id,
  }
}
const plusCart = (id) => {
  return {
    type: PLUS_CART,
    id,
  }
}
const deleteCart = (id) => {
  return {
    type: DELETE_CART,
    id,
  }
}
export {
  phoneLoaded,
  addPrice,
  addProduct,
  addLoading,
  addError,
  minusCart,
  plusCart,
  deleteCart,
}
