import * as Types from '../types'

export const phoneLoaded = (newBooks) => {
  return {
    type: Types.PHONE_LOADED,
    payload: newBooks,
  }
}

export const addPrice = (price) => {
  return {
    type: Types.ADD_PRICE,
    price: price,
  }
}

export const addProduct = (product) => {
  return {
    type: Types.ADD_PRODUCT,
    product: product,
  }
}
export const addLoading = (loading) => {
  return {
    type: Types.CHANGE_LOADING,
    loading,
  }
}
export const addError = (error) => {
  return {
    type: Types.CHANGE_ERROR,
    error,
  }
}
export const minusCart = (id) => {
  return {
    type: Types.MINUS_CART,
    id,
  }
}
export const plusCart = (id) => {
  return {
    type: Types.PLUS_CART,
    id,
  }
}
export const deleteCart = (id) => {
  return {
    type: Types.DELETE_CART,
    id,
  }
}
export const changeModal = (payload) => {
  return {
    type: Types.CHANGE_MODAL,
    payload,
  }
}
