const PHONE_LOADED = 'PHONE_LOADED'
const ADD_PRICE = 'ADD_PRICE'
const ADD_PRODUCT = 'ADD_PRODUCT'
const CHANGE_LOADING = 'LOADING'
const CHANGE_ERROR = 'ERROR'
const MINUS_CART = 'MINUS_CART'
const PLUS_CART = 'PLUS_CART'
const DELETE_CART = 'DELETE_CART'

export const phoneLoaded = (newBooks) => {
  return {
    type: PHONE_LOADED,
    payload: newBooks,
  }
}

export const addPrice = (price) => {
  return {
    type: ADD_PRICE,
    price: price,
  }
}

export const addProduct = (product) => {
  return {
    type: ADD_PRODUCT,
    product: product,
  }
}
export const addLoading = (loading) => {
  return {
    type: CHANGE_LOADING,
    loading,
  }
}
export const addError = (error) => {
  return {
    type: CHANGE_ERROR,
    error,
  }
}
export const minusCart = (id) => {
  return {
    type: MINUS_CART,
    id,
  }
}
export const plusCart = (id) => {
  return {
    type: PLUS_CART,
    id,
  }
}
export const deleteCart = (id) => {
  return {
    type: DELETE_CART,
    id,
  }
}
