import React from 'react'
import { useRouter } from 'next/router'
import { Modal, Button } from 'react-bootstrap'
import { connect } from 'react-redux'
import * as Action from '../../redux/actions'
import CartItem from './CartItem'
import back from './back.svg'
import close from './close.svg'

const CartEmpty = () => {
  return (
    <div className="cart_empty">
      <h2>Cart empty</h2>
    </div>
  )
}

const VerticalModal = ({
  modal,
  changeModal,
  cart,
  deleteCartItem,
  changeCount,
}) => {
  if (!modal) return null

  let cart_items = cart.map((item) => {
    return (
      <CartItem
        key={item.name + item.id}
        product={item}
        removeProduct={deleteCartItem}
        changeCount={changeCount}
        changeModal={changeModal}
      />
    )
  })

  let total_price = cart.reduce((previousValue, { price, count }) => {
    return previousValue + +price * +count
  }, 0)

  return (
    <>
      <style global="true">
        {`

        .cart_empty{
          padding: 100px 0;
          text-align:center;
        }
        .close_modal{
          padding: 10px;
          cursor: pointer;
        }



        .close_modal img{
          width: 15px;
        }
          .modal_header {
            display: flex;
            justify-content: space-between;
            align-items:center;
            padding: 10px 30px;
            border-bottom: 1px solid silver;
          }

          .modal_header h3{
              margin-bottom:0px;
              font-weight:400;
          }

          .modal_body{
            padding: 0 30px;
            flex:1;
          }

          .modal_body .product_item{
              padding: 15px 0;
          }

          .modal_body .image_preview img{
            max-width:70px;
            width:100%;
            height:auto;
          }
          .modal_body .remove_icon{
              font-size:20px;
              color:red;
          }

          .modal_body .full_price{
            font-size:18px;
          }


          .modal_footer{
            padding: 15px 30px;
            border-top:1px solid silver;
          }
          .modal_footer .total_price{
            font-size: 20px;
            margin-left:10px;
            font-weight:700;
          }
          .modal_footer .total{
            font-size: 20px;
          }
          .modal_footer .back_shopping_icon{
            width:10px;
          }

          .modal_footer .checkout_icon{
            font-size:12px;
            margin-left:6px;
          }

          .modal_footer .btn_checkout{
            font-size:16px;
          }

          @media (min-width: 576px){
            .modal-dialog {
                max-width: 700px;
                margin: 1.75rem auto;
            } 
          }

          @media screen and (max-width:576px){
            .modal{
              padding:0;
            }
            .modal_body{
              overflow-y:scroll;
            }
            .modal_body .full_price{
              font-size: 16px;
            }
            .modal-dialog-centered{
              height:100%;
              min-height:100%;
              max-height:100%;
            }
            .modal-content{
              height:100%;
            }
            .modal-dialog{
              margin:0;
            }
            .back_shopping{
              display: none;
            }
            .wrap_total_checkout{
              width:100%;
            }
            .modal_footer .total{
              display: flex;
              align-items:center;
              justify-content:space-between;
              margin-bottom:10px;
              margin-right:0;
            }
            .modal_footer {
              padding: 10px;
              border-top:none;
              box-shadow: 0px 0px 4px 0 #b1b1b1;
            }
            .modal_header {
              padding: 10px 20px;
              border-bottom:none;
              box-shadow: 0px 0px 4px 0 #b1b1b1;
            }
            .modal_footer .total .total_price{
              margin:0;
            }
            .modal_footer .checkout_icon{
              font-size: 14px;
            }
          }
        `}
      </style>
      <Modal
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        show={modal}
        onHide={() => changeModal(false)}
      >
        <div className="modal_header">
          <h3>Cart</h3>

          <div onClick={() => changeModal(false)} className="close_modal">
            <img src={close} className="close_icon" alt="" />
          </div>
        </div>

        {cart.length > 0 ? (
          <div className="modal_body">{cart_items}</div>
        ) : (
          <CartEmpty />
        )}

        <div className="modal_footer d-flex align-items-center justify-content-between">
          <a
            href="#"
            className="back_shopping text-decoration-none"
            onClick={() => changeModal(false)}
          >
            {' '}
            <img src={back} className="back_shopping_icon" alt="" /> Continue
            shopping
          </a>

          <div className="wrap_total_checkout d-flex align-items-sm-center flex-sm-row flex-column">
            <div className="total">
              <span>Total:</span>{' '}
              <span className="total_price">{total_price}$</span>
            </div>

            {cart.length > 0 && (
              <Button variant="primary" className="btn_checkout ml-sm-3 ml-0">
                Checkout{' '}
                <i
                  className="fa checkout_icon fa-credit-card-alt"
                  aria-hidden="true"
                ></i>
              </Button>
            )}
          </div>
        </div>
      </Modal>
    </>
  )
}

const mapStateToProps = ({ modal, cart }) => {
  if (!modal) return { modal }

  return { modal, cart }
}

const mapDispatchToProps = {
  changeModal: Action.changeModal,
  deleteCartItem: Action.deleteCart,
  changeCount: Action.changeCountById,
}

export default connect(mapStateToProps, mapDispatchToProps)(VerticalModal)
// export default VerticalModal
