import React, { Component } from 'react'
import Card from '../routes/shop/card'
import { connect } from 'react-redux'
import { withDataPhone } from '../components/hoc'
import { phoneLoaded, addLoading, addError } from '../redux/actions'
import Spinner from '../components/spinner'
import Error from '../components/error'
import styles from '../routes/shop.module.css'

const Shop = ({ phones }) => {
  let list = phones.map((item) => {
    return <Card {...item} key={item.id} />
  })
  return (
    <div className={styles.shop_content}>
      <div className="container">
        <div className="row">{list}</div>
      </div>
    </div>
  )
}

class ShopContainer extends Component {
  state = {
    loading: true,
    phones: [],
    error: false,
  }

  componentDidMount() {
    let { phoneservice } = this.props
    phoneservice
      .getPhones()
      .then((phones) => {
        this.setState({ loading: false, phones })
      })
      .catch((error) => {
        this.setState({ error, loading: false })
      })
  }

  render = () => {
    let { phones, loading, error } = this.state

    if (error) {
      return <Error />
    }
    if (loading) {
      return <Spinner />
    }

    return <Shop phones={phones} />
  }
}

export default withDataPhone()(ShopContainer)
