import React from 'react'
import { Consumer } from 'react-redux'

const withState = (Wrapped) => {
  return (props) => {
    return (
      <Consumer>
        {(store) => {
          return <Wrapped {...props} store={store} />
        }}
      </Consumer>
    )
  }
}

export default withState
