import React from 'react'
import { Modal, Button } from 'react-bootstrap'
import ProductCounter from '../../components/ProductCounter'

const { Header, Title, Body, Footer } = Modal

export default function VerticalModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <style global>
        {`
          .modal_header {
            display: flex;
            justify-content: space-between;
            align-items:center;
            padding: 15px 30px;
            border-bottom: 1px solid silver;
          }

          .modal_header .close_icon {
            font-size: 20px;
            cursor: pointer;
          }
          .modal_header h3{
              margin-bottom:0px;
          }

          .modal_body{
            padding: 0 30px;
          }

          .modal_body .product_item{
              padding: 15px 0;
          }

          .modal_body .image_preview img{
            max-width:70px;
            height:auto;
          }
          .remove_icon{
              font-size:20px;
              color:red;
          }
          .modal_footer{
            padding: 15px 30px;
            border-top:1px solid silver;
          }
          .modal_footer .total_price{
            margin-right: 15px;
            font-size: 20px;
          }
          .modal_footer .total{
            margin-right: 15px;
            font-size: 20px;
          }
          .modal_footer .total .total_price{
        margin-left:10px;
        font-weight:600;
          }
        `}
      </style>
      <div className="modal_header">
        <h3>Cart</h3>
        <div>
          <i class="fa fa-times close_icon" aria-hidden="true"></i>
        </div>
      </div>
      <div className="modal_body">
        <div className="product_item">
          <div className="row">
            <div className="col-2 d-flex align-items-center justify-content-center">
              <div className="image_preview">
                <img src="/image/1/preview.webp" alt="" />
              </div>
            </div>
            <div className="col-4 d-flex align-items-center">
              <a href="#">Realme C11</a>
            </div>

            <div className="col-3 d-flex align-items-center justify-content-center">
              <ProductCounter changeCount={(val) => {}} />
            </div>
            <div className="col-2 d-flex align-items-center justify-content-center">
              <span className="full_price">3000$</span>
            </div>
            <div className="col-1 d-flex align-items-center justify-content-center">
              <i class="fa fa-trash remove_icon" aria-hidden="true"></i>
            </div>
          </div>
        </div>
        <div className="product_item">
          <div className="row">
            <div className="col-2 d-flex align-items-center justify-content-center">
              <div className="image_preview">
                <img src="/image/1/preview.webp" alt="" />
              </div>
            </div>
            <div className="col-4 d-flex align-items-center">
              <a href="#">Realme C11</a>
            </div>

            <div className="col-3 d-flex align-items-center justify-content-center">
              <ProductCounter changeCount={(val) => {}} />
            </div>
            <div className="col-2 d-flex align-items-center justify-content-center">
              <span className="full_price">3000$</span>
            </div>
            <div className="col-1 d-flex align-items-center justify-content-center">
              <i class="fa fa-trash remove_icon" aria-hidden="true"></i>
            </div>
          </div>
        </div>
      </div>
      <div className="modal_footer d-flex align-items-center justify-content-between">
        <a href="#">Continue shopping</a>

        <div className="d-flex align-items-center">
          <div className="total">
            Total: <span className="total_price">12000$</span>
          </div>
          <Button variant="primary">Checkout</Button>
        </div>
      </div>
    </Modal>
  )
}
