import React from 'react'
import { connect } from 'react-redux'
import Link from 'next/link'

const Basket = ({ total_price, count_cart }) => {
  return (
    <Link href="/cart">
      <a className="basket_link">
        <div className="basket-header" id="basket-header">
          <i className="fa fa-shopping-basket basket-i"></i>
          <span className="basket-count">{count_cart} items</span>
          <span className="basket-cost">({total_price}$)</span>
        </div>
      </a>
    </Link>
  )
}

const mapStateToProps = ({ total_price, count_cart }) => {
  return {
    total_price,
    count_cart,
  }
}

export default connect(mapStateToProps)(Basket)
