import React from 'react'
import { Modal, Button } from 'react-bootstrap'
import { connect } from 'react-redux'
import * as Action from '../../redux/actions'
import ProductCounter from '../ProductCounter'
import back from './back.svg'
import close from './close.svg'

const VerticalModal = ({ modal, changeModal }) => {
  return (
    <>
      <style global>
        {`
          .modal_header {
            display: flex;
            justify-content: space-between;
            align-items:center;
            padding: 15px 30px;
            border-bottom: 1px solid silver;
          }

          .modal_header .close_icon {
            width: 15px;
            cursor: pointer;
          }
          .modal_header h3{
              margin-bottom:0px;
              font-weight:400;
          }

          .modal_body{
            padding: 0 30px;
            flex:1;
            overflow-y: scroll;
          }

          .modal_body .product_item{
              padding: 15px 0;
          }

          .modal_body .image_preview img{
            max-width:70px;
            width:100%;
            height:auto;
          }
          .modal_body .remove_icon{
              font-size:20px;
              color:red;
          }

          .modal_body .full_price{
            font-size:18px;
          }
          .modal_footer{
            padding: 15px 30px;
            border-top:1px solid silver;
          }
          .modal_footer .total_price{
            margin-right: 15px;
            font-size: 20px;
          }
          .modal_footer .total{
            margin-right: 15px;
            font-size: 20px;
          }
          .modal_footer .back_shopping_icon{
            width:10px;
            
          }
          .modal_footer .total .total_price{
            margin-left:10px;
            font-weight:700;
          }
          .modal_footer .checkout_icon{
            font-size:12px;
            margin-left:6px;
          }

          @media (min-width: 576px){
            .modal-dialog {
                max-width: 700px;
                margin: 1.75rem auto;
            } 
          }

          @media screen and (max-width:576px){
            .modal_body .full_price{
              font-size: 16px;
            }
            .modal-dialog-centered{
              height:100%;
              min-height:100%;
              max-height:100%;
            }
            .modal-content{
              height:100%;
            }
            .modal-dialog{
              margin:0;
            }
            .back_shopping{
              display: none;
            }
            .wrap_total_checkout{
              width:100%;
            }
            .modal_footer .total{
              display: flex;
              align-items:center;
              justify-content:space-between;
              margin-bottom:10px;
              margin-right:0;
            }
            .modal_footer {
              padding: 10px;
              border-top:none;
            }
            .modal_footer .total .total_price{
              margin:0;
            }
          }
        `}
      </style>
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <div className="modal_header">
          <h3>Cart</h3>
          <div>
            <img src={close} className="close_icon" alt="" />
          </div>
        </div>
        <div className="modal_body">
          <div className="product_item">
            <div className="row flex-nowrap">
              <div className="col-sm-2 col-3 px-0 d-flex align-items-center justify-content-center">
                <div className="image_preview">
                  <img src="/image/1/preview.webp" alt="" />
                </div>
              </div>
              <div className="col-sm-9 col-auto  d-flex align-items-center flex-nowrap flex-fill">
                <div className="row no-gutters w-100 flex-sm-row flex-column">
                  <div className="col-sm-4 col-auto">
                    <a href="#">Realme C11 Black 3/90</a>
                  </div>

                  <div className="col-8">
                    <div className="wrap mt-sm-0 mt-3 d-flex justify-content-sm-around justify-content-start">
                      <ProductCounter changeCount={(val) => {}} />
                      <span className="full_price ml-sm-0 ml-3">3000$</span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="col-1 d-flex align-items-center justify-content-center">
                <i class="fa fa-trash remove_icon" aria-hidden="true"></i>
              </div>
            </div>
          </div>
          <div className="product_item">
            <div className="row flex-nowrap">
              <div className="col-sm-2 col-3 px-0 d-flex align-items-center justify-content-center">
                <div className="image_preview">
                  <img src="/image/1/preview.webp" alt="" />
                </div>
              </div>
              <div className="col-sm-9 col-auto  d-flex align-items-center flex-nowrap flex-fill">
                <div className="row no-gutters w-100 flex-sm-row flex-column">
                  <div className="col-sm-4 col-auto">
                    <a href="#">Realme C11</a>
                  </div>

                  <div className="col-8">
                    <div className="wrap mt-sm-0 mt-3 d-flex justify-content-sm-around justify-content-start">
                      <ProductCounter changeCount={(val) => {}} />
                      <span className="full_price ml-sm-0 ml-3">3000$</span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="col-1 d-flex align-items-center justify-content-center">
                <i class="fa fa-trash remove_icon" aria-hidden="true"></i>
              </div>
            </div>
          </div>
        </div>
        <div className="modal_footer d-flex align-items-center justify-content-between">
          <a href="#" className="back_shopping">
            {' '}
            <img src={back} className="back_shopping_icon" alt="" /> Continue
            shopping
          </a>

          <div className="wrap_total_checkout d-flex align-items-sm-center flex-sm-row flex-column">
            <div className="total">
              <span>Total:</span> <span className="total_price">12 000$</span>
            </div>
            <Button variant="primary">
              Checkout{' '}
              <i
                class="fa checkout_icon fa-credit-card-alt"
                aria-hidden="true"
              ></i>
            </Button>
          </div>
        </div>
      </Modal>
    </>
  )
}

const mapStateToProps = ({ modal }) => {
  return { modal }
}

const mapDispatchToProps = {
  changeModal: Action.changeModal,
}

export default connect(mapStateToProps, mapDispatchToProps)(Modal)
