import React from 'react'
import { Modal, Button } from 'react-bootstrap'
import ProductCounter from '../../components/ProductCounter'
import back from './back.svg'
import close from './close.svg'

export default function VerticalModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <style global>
        {`
          .modal_header {
            display: flex;
            justify-content: space-between;
            align-items:center;
            padding: 15px 30px;
            border-bottom: 1px solid silver;
          }

          .modal_header .close_icon {
            width: 15px;
            cursor: pointer;
          }
          .modal_header h3{
              margin-bottom:0px;
          }

          .modal_body{
            padding: 0 30px;
          }

          .modal_body .product_item{
              padding: 15px 0;
          }

          .modal_body .image_preview img{
            max-width:70px;
            height:auto;
          }
          .remove_icon{
              font-size:20px;
              color:red;
          }
          .modal_footer{
            padding: 15px 30px;
            border-top:1px solid silver;
          }
          .modal_footer .total_price{
            margin-right: 15px;
            font-size: 20px;
          }
          .modal_footer .total{
            margin-right: 15px;
            font-size: 20px;
          }
          .modal_footer .back_shopping_icon{
            width:10px;
            
          }
          .modal_footer .total .total_price{
            margin-left:10px;
            font-weight:700;
          }
          .modal_footer .checkout_icon{
            font-size:12px;
            margin-left:6px;
          }

          @media (min-width: 576px){
            .modal-dialog {
                max-width: 700px;
                margin: 1.75rem auto;
            }
            
          }
        `}
      </style>
      <div className="modal_header">
        <h3>Cart</h3>
        <div>
          <img src={close} className="close_icon" alt="" />
        </div>
      </div>
      <div className="modal_body">
        <div className="product_item">
          <div className="row">
            <div className="col-2 d-flex align-items-center justify-content-center">
              <div className="image_preview">
                <img src="/image/1/preview.webp" alt="" />
              </div>
            </div>
            <div className="col-9 d-flex align-items-center">
              <div className="row no-gutters w-100 flex-sm-row flex-column">
                <div className="col-4">
                  <a href="#">Realme C11</a>
                </div>

                <div className="col-8">
                  <div className="wrap mt-sm-0 mt-3 d-flex justify-content-sm-around justify-content-start">
                    <ProductCounter changeCount={(val) => {}} />
                    <span className="full_price">3000$</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="col-1 d-flex align-items-center justify-content-center">
              <i class="fa fa-trash remove_icon" aria-hidden="true"></i>
            </div>
          </div>
        </div>
      </div>
      <div className="modal_footer d-flex align-items-center justify-content-between">
        <a href="#">
          {' '}
          <img src={back} className="back_shopping_icon" alt="" /> Continue
          shopping
        </a>

        <div className="d-flex align-items-center">
          <div className="total">
            Total: <span className="total_price">12 000$</span>
          </div>
          <Button variant="primary">
            Checkout{' '}
            <i
              class="fa checkout_icon fa-credit-card-alt"
              aria-hidden="true"
            ></i>
          </Button>
        </div>
      </div>
    </Modal>
  )
}
