import { useEffect, useState } from 'react'
import { useRouter } from 'next/router'

function Post({ serverPost }) {
  let { id } = useRouter().query

  let [post, setPost] = useState(serverPost)

  async function load() {
    const res = await fetch(`http://localhost:4000/posts/${id}`)
    const p = await res.json()
    setPost(p)
  }
  useEffect(() => {
    if (!post) {
      load()
    }
  })

  if (!post) {
    return <h1>...Loading</h1>
  }

  let label = `Post: `

  return <h1>POST: </h1>
}

Post.getInitialProps = async ({ query, req }) => {
  if (!req) return { serverPost: null }

  let { id } = query
  const res = await fetch(`http://localhost:4000/phones/${id}`)
  const serverPost = await res.json()

  return {
    serverPost,
  }
}

export default Post
