import React, { Component } from 'react'
import Card from '../routes/shop/card'
import { withDataPhone } from '../components/hoc'
import Spinner from '../components/spinner'
import Error from '../components/error'
import styles from '../routes/shop/shop.module.css'
import PhoneService from '../service/phone-service'

const Shop = ({ phones }) => {
  let list = phones.map((item) => {
    return <Card {...item} key={item.id} />
  })
  return (
    <div className={styles.shop_content}>
      <div className="container">
        <div className="row">{list}</div>
      </div>
    </div>
  )
}

class ShopContainer extends Component {
  static async getInitialProps({ req }) {
    let phones = null
    if (!req) {
      return { phones }
    }

    try {
      // console.log(PhoneService.getPhones())
      phones = await PhoneService.getResource('')
      // const res = await fetch('http://localhost:4000/phones')
      // phones = await res.json()
    } catch (error) {
      console.log('ERRORRRRRRR ', error)
      return { error }
    }

    return { phones }
  }

  async load() {
    try {
      let res = await PhoneService.getPhones()
      // let res = await fetch('http://localhost:4000/phones')
      let phones = await res.json()
      this.setState({ loading: false, phones })
    } catch (error) {
      this.setState({ error, loading: false })
    }
  }

  state = {
    loading: !this.props.phones,
    phones: this.props.phones,
    error: this.props.error,
  }

  componentDidMount() {
    if (!this.state.phones) {
      this.load()
    }
  }

  render = () => {
    let { phones, loading, error } = this.state

    if (error) {
      return <Error />
    }
    if (loading || !phones) {
      return <Spinner />
    }

    return <Shop phones={phones} />
  }
}

export default ShopContainer
