import React from 'react'
import { ReactReduxContext } from 'react-redux'

const withState = (Wrapped) => {
  return (props) => {
    return (
      <ReactReduxContext.Consumer>
        {({ store }) => {
          return <Wrapped {...props} store={store} />
        }}
      </ReactReduxContext.Consumer>
    )
  }
}

export default withState
